     */
    public function testGetLoginUrl()
    {
        $hash = md5(uniqid());

        // for online, auto approve, state
        $token = 'foobar_token';
        $actual = $this->object
            ->forOnline()
            ->autoApprove()
            ->setState($hash)
            ->setDisplay('web')
