        $this->assertEquals(
                $this->urlRequest . '?response_type=code' .
                '&client_id=' . $this->clientId .
                '&redirect_uri=' . urlencode($this->urlRedirect) .
                '&access_type=online' .
                '&approval_prompt=auto' .
                '&state=' . $hash .
                '&display=web', $actual);

        // test offline, force approve, scope
        $actual = $this->object
            ->forOffline()
            ->forceApprove()
            ->setScope('readOnly')
            ->getLoginUrl();

        $this->assertEquals(
                $this->urlRequest . '?response_type=code' .
                '&client_id=' . $this->clientId .
                '&redirect_uri=' . urlencode($this->urlRedirect) .
                '&access_type=offline' .
                '&approval_prompt=force' .
                '&scope=readOnly' .
                '&state=' . $hash .
                '&display=web', $actual);
    }

